<?php$route = '/ongithub/videos/';$app->get($route, function ()  use ($app){	if(isset($_REQUEST['query'])){ $query = $_REQUEST['query']; } else { $query = '';}	$ObjectText = file_get_contents('https://raw.github.com/ongithub/videos/gh-pages/videos.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 1;		$id = $Object['id'];		$name = $Object['name'];		$description = $Object['description'];		$url = $Object['url'];		$video = $Object['video'];		$thumbnailUrl = $Object['thumbnailUrl'];		$keywords = $Object['keywords'];		$inLanguage = $Object['inLanguage'];				$creator = $Object['creator'];		if($query!=''){		$IncludeRecord = 0;			if(strpos(strtolower($name),strtolower($query)) === 0 || strpos(strtolower($description),strtolower($query)) === 0)				{				$IncludeRecord = 1;				}			}		if($IncludeRecord==1)			{					$F = array();			$F['id'] = $id;			$F['name'] = $name;			$F['description'] = $description;			$F['url'] = $url;			$F['video'] = $video;			$F['thumbnailUrl'] = $thumbnailUrl;			$F['keywords'] = $keywords;			$F['inLanguage'] = $inLanguage;			$F['creator'] = $creator;						array_push($ReturnObject, $F);			}		}		$app->response()->header("Content-Type", "application/json");		echo format_json(json_encode($ReturnObject));	});$route = '/ongithub/videos/:id';$app->get($route, function ($IncomingID)  use ($app){	$ObjectText = file_get_contents('https://raw.github.com/ongithub/videos/gh-pages/videos.json');	$ObjectResult = json_decode($ObjectText,true);	$ReturnObject = array();	foreach($ObjectResult as $Object){		$IncludeRecord = 0;		$id = $Object['id'];		$name = $Object['name'];		$description = $Object['description'];		$url = $Object['url'];		$video = $Object['video'];		$thumbnailUrl = $Object['thumbnailUrl'];		$keywords = $Object['keywords'];		$inLanguage = $Object['inLanguage'];				$creator = $Object['creator'];		if($Incomi